#include <stdio.h>
#include <string.h>

int most_frequent_digit(int n) {
    int counter[10] = {0};
    for (int i = 1; i <= n; i++) {
        char str[10];
        sprintf(str, "%d", i);
        for (int j = 0; j < strlen(str); j++) {
            counter[str[j] - '0']++;
        }
    }
    int max_count = 0;
    int most_frequent = 0;
    for (int k = 0; k < 10; k++) {
        if (counter[k] > max_count) {
            max_count = counter[k];
            most_frequent = k;
        }
    }
    return most_frequent;
}

int main() {
    printf("%d", most_frequent_digit(100));
    return 0;
}
